import Expense from "./components/Expense/Expense";
import NewExpense from "./components/New Expense/NewExpense";
import { useState } from "react";
function App() {
  const DUMMY_EXPENSES = [
    {
      id: 'e1',
      title: 'Toilet Paper',
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: 'e3',
      title: 'Car Insurance',
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: 'e4',
      title: 'New Desk (Wooden)',
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];
  [getExpense,setExpense] = useState(DUMMY_EXPENSES)

  const AddNewExpense = (newExpenseData)=>{
    setExpense((newExpenseData) => [...getExpense,newExpenseData])

  }
  return (
    <div>
    <NewExpense onAddNewExpense = {AddNewExpense}></NewExpense>
    <Expense items = {getExpense}/>
    </div>
  );
}

export default App;
