import './Expense.css'
import Card from '../UI/Card'
import ExpenseItem from './ExpenseItem'
import ExpenseFilter from './ExpenseFilter'
import { useState } from 'react'
const Expense = (props) => {
   const {filterYear,setFilterYear} = useState('2020')
   const handleChangeExpenseFilter = (chosenYear) =>{
    setFilterYear(chosenYear)
   }
   let filterExpenseList = props.items.map( expense => expense.date.getFullYear() == filterYear && <ExpenseItem key = {expense.id} title = {expense.title} amount = {expense.amount} date = {expense.date} /> )
return (
    <Card className = 'expenses'>
      <ExpenseFilter selectedValue = {filterYear} handleExpenseFilter = {handleChangeExpenseFilter}></ExpenseFilter>
      {filterExpenseList}
    </Card>);

}

export default Expense
